function getQueryVariable(e){for(var r=window.location.search.substring(1),a=r.split("&"),t=0;t<a.length;t++){var s=a[t].split("=");if(s[0]==e)return s[1]}return!1}if("true"===getQueryVariable("password")||"true"===getQueryVariable("pw")||"true"===getQueryVariable("pwpassword")){var elem=document.querySelector("#password-container");elem.classList.add("password-show")}